import React from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import {
  TextField,
  Typography,
  Button,
  IconButton,
  Box,
} from "@material-ui/core";
import Arrow from "@material-ui/icons/Send";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLinkedin, faGithub } from "@fortawesome/free-brands-svg-icons";
import InputAdornment from "@material-ui/core/InputAdornment";
import AccountCircle from "@material-ui/icons/AccountCircle";

const useStyles = makeStyles((theme) => ({
  contactContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: theme.palette.accent2.main,
    height: "600px",
    // border: "2px white solid",
  },
  form: {
    background: theme.palette.primary.main,
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    width: "80%",
    // border: "2px white solid",
    padding: "2rem 2rem 3rem",
    margin: "0 0 3rem 0",
    borderRadius: "1.2rem",

    [theme.breakpoints.up("md")]: {
      width: "55%",
    },

    // top: "50%",
    // left: "50%",
    // transform: "translate(-50%, -50%)",
    // position: "absolute",
  },
  button: {
    // margin: "1rem 0 0 0",
    // color: "tomato",
    // borderColor: "tomato",
    width: "100px",
    margin: "1rem 0 0 0",
    textTransform: "lowercase",
    background: theme.palette.accent2.main,
    color: "white",
  },
  heading: {
    // color: "tomato",
    textAlign: "center",
    // textTransform: "uppercase",
    margin: "0 0 1rem 0",
  },
  socialContainer: {
    display: "flex",
    flexDirection: "column",

    justifyContent: "center",
    alignItems: "center",
  },
  socialContainerIcons: {
    display: "flex",
  },
  // socialContainerIconsLabels: {
  //   display: "flex",
  //   flexDirection
  // },
  iconStyle: {
    margin: "0 1rem",
    color: theme.palette.accent1.main,
  },
}));

// const InputField = withStyles({
//   // https://material-ui.com/customization/components/ docs here
//   // withStyles, not makeStyles
//   root: {
//     "& label.Mui-focused": {
//       // find these classes inspect element, styles
//       color: "tomato",
//     },
//     "& label": {
//       color: "tomato",
//     },
//     "& .MuiOutlinedInput-root": {
//       "& fieldset": {
//         borderColor: "tan",
//       },
//       "&:hover fieldset": {
//         borderColor: "tan",
//       },
//       "&.Mui-focused fieldset": {
//         borderColor: "tan",
//       },
//     },
//   },
// })(TextField);

const Contact = () => {
  const classes = useStyles();
  return (
    <Box className={classes.contactContainer} component="div">
      <Box component="form" className={classes.form}>
        <Typography className={classes.heading} variant="h5">
          contact me
        </Typography>
        <TextField
          fullWidth={false}
          label="your contact info (optional)"
          // variant="filled"
          // variant="outlined"
          // inputProps={{ style: { color: "white" } }} // this is how to change the input text color
          // margin="dense"
          size="medium"
          multiline
        />
        <br />
        <TextField
          fullWidth={true}
          label="tell me a joke, drop your linked in, feedback about the site, food recommendations?"
          // variant="filled"
          margin="dense"
          size="medium"
          // multiline={true}
          rows="3"
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <AccountCircle />
              </InputAdornment>
            ),
          }}
        />
        <br />

        <Button
          variant="contained"
          className={classes.button}
          endIcon={<Arrow>send</Arrow>}
        >
          Send
        </Button>
      </Box>
      <Box className={classes.socialContainer}>
        <Typography className={classes.heading} variant="h5">
          find me online
        </Typography>
        <Box className={classes.socialContainerIcons}>
          <IconButton href="https://www.linkedin.com/in/kellhe/">
            <Box className={classes.socialContainer}>
              <FontAwesomeIcon
                icon={faLinkedin}
                size="2x"
                className={classes.iconStyle}
              />
              <Typography style={{ fontSize: "1rem" }}>linkedin</Typography>
            </Box>
          </IconButton>

          <IconButton href="https://github.com/kellyhe8">
            <Box className={classes.socialContainer}>
              <FontAwesomeIcon
                icon={faGithub}
                size="2x"
                className={classes.iconStyle}
              />
              <Typography style={{ fontSize: "1rem" }}>
                personal github
              </Typography>
            </Box>
          </IconButton>
          <IconButton href="https://github.com/kellyhesf">
            <Box className={classes.socialContainer}>
              <FontAwesomeIcon
                icon={faGithub}
                size="2x"
                className={classes.iconStyle}
              />
              <Typography style={{ fontSize: "1rem" }}>work github</Typography>
            </Box>
          </IconButton>
        </Box>
      </Box>
    </Box>
  );
};

export default Contact;
