import React, { useState } from "react";
import { Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import MobileRightMenuSlider from "@material-ui/core/Drawer";

import {
  AppBar,
  Toolbar,
  ListItem,
  ListItemIcon,
  IconButton,
  ListItemText,
  Avatar,
  Divider,
  List,
  Typography,
  Box,
} from "@material-ui/core";

import {
  Menu,
  AssignmentInd,
  Home,
  Apps,
  ContactMail,
  // Block,
} from "@material-ui/icons";

import avatar from "../img/me.jpg";

// CSS STYLES
const useStyles = makeStyles((theme) => ({
  menuSliderContainer: {
    width: 250,
    height: "100%",
  },
  avatar: {
    display: "block",
    margin: "2rem auto",
    width: theme.spacing(13), // different way to set size of avatar pic
    height: theme.spacing(13),
    border: `.1rem ${theme.palette.accent1.main} solid`,
  },
  menuItem: {
    margin: "1rem auto",
  },
  toolbarContent: {
    width: "55%",
    display: "flex",
    justifyContent: "space-between",
  },
  highlight: {
    color: theme.palette.accent1.main,
  },
}));

const menuIcons = [
  {
    listIcon: <Home />,
    listText: "Home",
    listPath: "/",
  },
  {
    listIcon: <AssignmentInd />,
    listText: "Resume",
    listPath: "/resume",
  },
  {
    listIcon: <Apps />,
    listText: "Blog",
    listPath: "/blog",
  },
  {
    listIcon: <ContactMail />,
    listText: "Contact Me",
    listPath: "/blog",
  },
];

const Navbar = () => {
  const [state, setState] = useState({
    right: false,
  });

  const toggleSlider = (slider, open) => () => {
    setState({ ...state, [slider]: open });
  };

  const classes = useStyles();

  const sideList = (slider) => (
    <Box
      className={classes.menuSliderContainer}
      component="div"
      onClick={toggleSlider(slider, false)}
    >
      <Avatar className={classes.avatar} src={avatar} alt="kelly he"></Avatar>
      <Divider />
      <List>
        {menuIcons.map((lsItem, key) => (
          <ListItem button key={key} component={Link} to={lsItem.listPath}>
            <ListItemIcon
              className={`${classes.listItem} ${classes.highlight}`}
            >
              {lsItem.listIcon}
            </ListItemIcon>
            <ListItemText
              className={classes.listItem}
              primary={lsItem.listText}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <Box component="nav">
        <AppBar position="static" style={{ alignItems: "center" }}>
          <Toolbar className={classes.toolbarContent}>
            <Typography variant="h5">
              hey i'm <span className={classes.highlight}>kelly</span>
            </Typography>
            <IconButton onClick={toggleSlider("right", true)}>
              <Menu />
            </IconButton>

            <MobileRightMenuSlider
              anchor="right"
              open={state.right}
              onClose={toggleSlider("right", false)}
            >
              {sideList("right")}
            </MobileRightMenuSlider>
          </Toolbar>
        </AppBar>
      </Box>
    </>
  );
};

export default Navbar;

/*
- adding the button tag makes it do the clicky animation
- anchor="right" makes menu open from the right side instead of left. can also do "top" or "bottom"
*/
