import React from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import {
  TextField,
  Typography,
  Button,
  Grid,
  Box,
  Link,
} from "@material-ui/core";
import Arrow from "@material-ui/icons/Send";
import Footer from "./Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLinkedin, faGithubSquare } from "@fortawesome/free-brands-svg-icons";

const useStyles = makeStyles((theme) => ({
  contactContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: theme.palette.accent2.main,
    height: "600px",
    // border: "2px white solid",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    width: "45%",
    // border: "2px white solid",
    margin: "2rem 0 2rem 0",

    // top: "50%",
    // left: "50%",
    // transform: "translate(-50%, -50%)",
    // position: "absolute",
  },
  button: {
    // margin: "1rem 0 0 0",
    // color: "tomato",
    // borderColor: "tomato",
    width: "100px",
    margin: "1rem 0 0 0",
  },
  heading: {
    // color: "tomato",
    textAlign: "center",
    // textTransform: "uppercase",
    margin: "0 0 1rem 0",
  },
  socialContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

// const InputField = withStyles({
//   // https://material-ui.com/customization/components/ docs here
//   // withStyles, not makeStyles
//   root: {
//     "& label.Mui-focused": {
//       // find these classes inspect element, styles
//       color: "tomato",
//     },
//     "& label": {
//       color: "tomato",
//     },
//     "& .MuiOutlinedInput-root": {
//       "& fieldset": {
//         borderColor: "tan",
//       },
//       "&:hover fieldset": {
//         borderColor: "tan",
//       },
//       "&.Mui-focused fieldset": {
//         borderColor: "tan",
//       },
//     },
//   },
// })(TextField);

const Contact = () => {
  const classes = useStyles();
  return (
    <Box className={classes.contactContainer} component="div">
      <Box component="form" className={classes.form}>
        <Typography className={classes.heading} variant="h5">
          contact me
        </Typography>
        <TextField
          fullWidth={false}
          label="your contact info (optional)"
          // variant="outlined"
          // inputProps={{ style: { color: "white" } }} // this is how to change the input text color
          // margin="dense"
          size="medium"
        />
        <br />
        <TextField
          fullWidth={true}
          label="tell me a joke, drop your linked in, give me some feedback about the site, food recommendations?"
          // variant="outlined"
          // margin="dense"
          size="medium"
        />
        <br />

        <Button
          variant="contained"
          color="primary"
          className={classes.button}
          endIcon={<Arrow>send</Arrow>}
        >
          Send
        </Button>
      </Box>
      <div className={classes.socialContainer}>
        <Typography className={classes.heading} variant="h5">
          find me online
        </Typography>
        <a href="https://www.linkedin.com/in/kellhe/">
          <Button>
            <FontAwesomeIcon icon={faLinkedin} />

            {/* <LinkedIn style={{ fontSize: "3rem" }} /> */}
          </Button>
        </a>
        {/* <a
          href="https://github.com/kellyhesf"
          class="fa fa-github"
          style={{ fontSize: "1.5em" }}
        ></a> */}

        <a href="https://github.com/kellyhe8">
          <Button>
            <FontAwesomeIcon icon={faGithubSquare} size="3rem" />
            {/* <GitHub style={{ fontSize: "3rem" }} /> */}
          </Button>
        </a>
      </div>

      <Footer />
    </Box>
  );
};

export default Contact;
