import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Typography, Box } from "@material-ui/core";
import WorkExperienceEntry from "./WorkExperienceEntry";

const useStyles = makeStyles((theme) => ({
  mainContainer: {
    background: theme.palette.primary.dark,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  contentArea: {
    width: "95%",
    [theme.breakpoints.up("lg")]: {
      // larger screens 80% width
      width: "80%",
    },
  },
  timeline: {
    position: "relative",
    padding: "1rem",
    margin: "0 auto",
    "&:before": {
      content: "''", // need double quotes otherwise wont' work?
      position: "absolute",
      height: "100%",
      border: `1px white solid`,
      right: "40px",
      top: 0,
    },
    "&:after": {
      content: "''", // need double quotes otherwise wont' work?
      display: "table",
      clear: "both",
    },
    [theme.breakpoints.up("md")]: {
      // medium screens centered. 960 px
      padding: "0rem",
      "&:before": {
        left: "calc(50% - 1px)",
        right: "auto",
      },
    },
  },
  heading: {
    // color: "tomato",
    padding: "3rem 0",
    textTransform: "lowercase",
  },

  timelineItem: {
    padding: "1rem",
    borderBottom: `2px solid ${theme.palette.accent2.main}`,
    position: "relative",
    margin: "1rem 3rem 1rem 1rem",
    clear: "both",
    // arrow
    "&:before": {
      content: "''",
      position: "absolute",
      right: "-0.625rem",
      top: "calc(50% - 5px)",
      borderStyle: "solid",
      borderColor: `${theme.palette.accent2.main} ${theme.palette.accent2.main} transparent transparent`,
      borderWidth: "0.625rem",
      transform: "rotate(45deg)",
    },
    "&:after": {
      content: "''",
      position: "absolute",
    },
    [theme.breakpoints.up("md")]: {
      width: "44%",
      margin: "1rem",
      // float: "left",
      "&:nth-of-type(2n)": {
        float: "right",
        margin: "1rem",
        borderColor: theme.palette.accent2.main, // bottom line of item
      },
      "&:nth-of-type(2n):before": {
        right: "auto",
        left: "-0.625rem",
        borderColor: `transparent transparent ${theme.palette.accent2.main} ${theme.palette.accent2.main}`,
      }, // the before is the arrow
    },
  },
  timelineYear: {
    textAlign: "center",
    maxWidth: "6.5rem",
    margin: "0 2rem 0 auto",
    fontSize: "1.5rem",
    background: theme.palette.accent1.main,
    // color: "white",
    lineheight: 1,
    padding: "0.5rem 1rem", // 0.5rem 0 1rem
    borderRadius: "1.2rem",
    "&:before": {
      display: "none",
    },
    [theme.breakpoints.up("md")]: {
      textAlign: "center",
      margin: "0 auto",
      "&:nth-of-type(2n)": {
        float: "none",
        margin: "0 auto",
      },
      "&:nth-of-type(2n):before": {
        display: "none",
      },
    },
  },
}));

const WorkExperience = () => {
  const classes = useStyles();
  return (
    <>
      {/* <Navbar /> */}
      <Box className={classes.mainContainer} component="header">
        <Box className={classes.contentArea}>
          <Typography className={classes.heading} variant="h4" align="center">
            software experience
          </Typography>
          <Box className={classes.timeline} component="div">
            <Typography
              className={`${classes.timelineYear} ${classes.timelineItem}`}
              variant="h2"
            >
              2013
            </Typography>
            <WorkExperienceEntry
              company="Salesforce"
              role="Software Engineering Intern"
              description="Hello this is desc"
            />
            <WorkExperienceEntry
              company="Youth Global Network: Project-C"
              role="Software Engineering Intern"
              description="Hello this is desc"
            />
            <Typography
              className={`${classes.timelineYear} ${classes.timelineItem}`}
              variant="h2"
            >
              2013
            </Typography>
          </Box>
        </Box>
      </Box>
    </>
  );
};

export default WorkExperience;
