import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import { Typography, Box } from "@material-ui/core";

const styles = (theme) => ({
  timelineItem: {
    padding: "1rem",
    borderBottom: `2px solid ${theme.palette.accent2.main}`,
    position: "relative",
    margin: "1rem 3rem 1rem 1rem",
    clear: "both",
    // arrow
    "&:before": {
      content: "''",
      position: "absolute",
      right: "-0.625rem",
      top: "calc(50% - 5px)",
      borderStyle: "solid",
      borderColor: `${theme.palette.accent2.main} ${theme.palette.accent2.main} transparent transparent`,
      borderWidth: "0.625rem",
      transform: "rotate(45deg)",
    },
    "&:after": {
      content: "''",
      position: "absolute",
    },
    [theme.breakpoints.up("md")]: {
      width: "44%",
      margin: "1rem",
      // float: "left",
      "&:nth-of-type(2n)": {
        float: "right",
        margin: "1rem",
        borderColor: theme.palette.accent2.main, // bottom line of item
      },
      "&:nth-of-type(2n):before": {
        right: "auto",
        left: "-0.625rem",
        borderColor: `transparent transparent ${theme.palette.accent2.main} ${theme.palette.accent2.main}`,
      }, // the before is the arrow
    },
  },
  timelineYear: {
    textAlign: "center",
    maxWidth: "6.5rem",
    margin: "0 2rem 0 auto",
    fontSize: "1.5rem",
    background: theme.palette.accent1.main,
    // color: "white",
    lineheight: 1,
    padding: "0.5rem 1rem", // 0.5rem 0 1rem
    borderRadius: "1.2rem",
    "&:before": {
      display: "none",
    },
    [theme.breakpoints.up("md")]: {
      textAlign: "center",
      margin: "0 auto",
      "&:nth-of-type(2n)": {
        float: "none",
        margin: "0 auto",
      },
      "&:nth-of-type(2n):before": {
        display: "none",
      },
    },
  },
  companyStyle: {
    // color: "white",
    textAlign: "center",
    padding: "0 0 .3rem 0",
    textTransform: "lowercase",
  },
  roleStyle: {
    color: theme.palette.accent1.main,
    textAlign: "center",
    padding: "0 0 .6rem 0",
    // textTransform: "lowercase",
  },
  descriptionStyle: {
    // color: theme.palette.accent1.main,
    fontSize: "1rem",
    fontWeight: 100,
    textAlign: "justify",
    // textTransform: "lowercase",
  },
});

const WorkExperienceEntry = (props) => {
  const { classes } = props;

  return (
    <>
      <Box className={classes.timelineItem} component="div">
        <Typography className={classes.companyStyle} variant="h5">
          {props.company}
        </Typography>
        <Typography className={classes.roleStyle} variant="body1">
          {props.role} | {props.location} <br />
          {props.date}
        </Typography>
        <Typography className={classes.descriptionStyle} variant="subtitle1">
          {props.description}
        </Typography>
        <video
          width="200"
          height="400"
          align="center"
          src={props.video ? props.video : null}
          controls
        ></video>
      </Box>
    </>
  );
};

export default withStyles(styles)(WorkExperienceEntry);
